import React from 'react';
import './Projects.css';

function Projects() {
  const projectData = [
    {
      title: 'Random Project 1',
      description: 'This is a description for the first random project.',
      image: 'https://via.placeholder.com/300x200.png?text=Project+1',
      link: 'https://example.com/project1',
    },
    {
      title: 'Random Project 2',
      description: 'This is a description for the second random project.',
      image: 'https://via.placeholder.com/300x200.png?text=Project+2',
      link: 'https://example.com/project2',
    },
  ];

  return (
    <div className="projects">
      <h1 className="projects-title">My Projects</h1>
      <p className="projects-message">
        We are working on these projects and will be back soon. Thank you for your patience!
      </p>
      <div className="projects-grid">
        {projectData.map((project, index) => (
          <a
            href={project.link}
            target="_blank"
            rel="noopener noreferrer"
            className="project-link"
            key={index}
          >
            <div className="project-card">
              <img src={project.image} alt={project.title} className="project-image" />
              <h3>{project.title}</h3>
              <p>{project.description}</p>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default Projects;
